import React, { Component } from "react";
import "./ContactForm.css";

const emailRegex = RegExp(/^[a-zA-Z0-9.!#$%&’*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/);

const formValid = ({ formErrors, ...rest }) => {
    let valid = true;
    // validate form errors being empty
    Object.values(formErrors).forEach(val => {
      val.length > 0 && (valid = false);
    });
    // validate the form was filled out
    Object.values(rest).forEach(val => {
      val === null && (valid = false);
    });
    return valid;
};


class App extends Component {
    constructor(props) {
        super(props);
        this.state = {  firstName:  null,
                        lastName:   null,
                        email:      null,
                        message:   null,
                        formErrors: { firstName: "", lastName: "", email: "", message: "" }
                     };
    }

    handleSubmit = e => {
        e.preventDefault();

        if (formValid(this.state)) {
            console.log(`--Submitting--
                         First Name: ${this.state.firstName}
                         Last Name: ${this.state.lastName}
                         Email: ${this.state.email}
                         Message: ${this.state.message}`);
        }
        else {
          console.error("Invalid Form");
        }
    };

    handleChange = e => {
        e.preventDefault();
        const { formItem, value } = e.target;
        let formErrors = { ...this.state.formErrors };

        switch (formItem) {
            case "firstName":
                formErrors.firstName = value.length < 3 ? "minimum 3 characaters required" : "";
            break;
            case "lastName":
                formErrors.lastName = value.length < 3 ? "minimum 3 characaters required" : "";
            break;
            case "email":
                formErrors.email = emailRegex.test(value) ? "" : "invalid email address";
            break;
            case "message":
                formErrors.message = value.length < 10 ? "minimum 10 characaters required" : "";
            break;
            default:
            break;
        }

        this.setState({ formErrors, [name]: value }, () => console.log(this.state));
    };

    render() {
        const { formErrors } = this.state;
        return (
            <div className="form-wrapper">
                <h1>Email Me</h1>
                <form onSubmit={this.handleSubmit} noValidate>

                    <div className="firstName">
                        <label htmlFor="firstName">First Name</label>
                        <input className={formErrors.firstName.length > 0 ? "error" : null}
                               placeholder="First Name"
                               type="text"
                               name="firstName"
                               noValidate
                               onChange={this.handleChange}/>
                        {formErrors.firstName.length > 0 && (
                            <span className="errorMessage">{formErrors.firstName}</span>
                        )}
                    </div>

                    <div className="lastName">
                        <label htmlFor="lastName">Last Name</label>
                        <input className={formErrors.lastName.length > 0 ? "error" : null}
                               placeholder="Last Name"
                               type="text"
                               name="lastName"
                               noValidate
                               onChange={this.handleChange}/>
                        {formErrors.lastName.length > 0 && (
                            <span className="errorMessage">{formErrors.lastName}</span>
                        )}
                    </div>

                    <div className="email">
                        <label htmlFor="email">Email</label>
                        <input className={formErrors.email.length > 0 ? "error" : null}
                               placeholder="Email"
                               type="email"
                               name="email"
                               noValidate
                               onChange={this.handleChange}/>
                        {formErrors.email.length > 0 && (
                            <span className="errorMessage">{formErrors.email}</span>
                        )}
                    </div>

                    <div className="message">
                      <label htmlFor="message">message</label>
                      <input
                        className={formErrors.message.length > 0 ? "error" : null}
                        placeholder="message"
                        type="message"
                        name="message"
                        noValidate
                        onChange={this.handleChange}
                      />
                      {formErrors.message.length > 0 && (
                        <span className="errorMessage">{formErrors.message}</span>
                      )}
                    </div>

                  <div className="createAccount">
                    <button type="submit">Submit</button>
                  </div>

                </form>
            </div>
        );
    }
}

export default App;
