import React from 'react';
import './Gallery.css'

/* Format: ['image.jpg', 'title', 'description', 'more info?'], */
const images = [ ['pump_bracket.jpg', 'Pump Bracket', 'A pump bracket that I manufactured for my custom-built computer.'],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', true],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', false],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', false],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', false],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', false],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', false],
                 ['some_other_image.jpg', 'Some Image', 'Some Description', false]
               ];

class Gallery extends React.Component {
    getImage(n) {
        return <img src={images[n][0]} alt={images[n][0]}/>
    }
    getInfo(n) {
        return(
            <div className="img-info">
                <div className="content">
                    <h2>{images[n][1]}</h2>
                    <p>{images[n][2]}</p>
                </div>
            </div>
        )
    }
    getItem(n) {
        return(
            <div className="box">
                <div className="img-box">{this.getImage(n)}</div>
                {this.getInfo(n)}
            </div>
        )
    }

    render() {
        return(
          <div className="page-container">
              <header className="gallery-header">Gallery</header>
              <div className="gallery-container">
                  <a href="/">{this.getItem(0)}</a>
                  {this.getItem(1)}
                  {this.getItem(2)}
                  {this.getItem(3)}
                  {this.getItem(4)}
                  {this.getItem(5)}
                  {this.getItem(6)}
                  {this.getItem(7)}
              </div>
          </div>
        )
    }
}

export default Gallery
